mapper.select:
SELECT o.*, u.user_name, u.user_avatar, p.name AS product_title, p.cover_list AS product_cover, ad.concat_person, ad.concat_phone, ad.get_adr, ad1.concat_person AS adr_name, ad1.concat_phone AS adr_phone, ad1.get_adr AS adr FROM orders o LEFT JOIN user u ON u.id = o.user_id LEFT JOIN product p ON p.id = o.product_id LEFT JOIN address ad ON ad.id = o.address_id LEFT JOIN product pt ON pt.id = o.product_id LEFT JOIN user u1 ON pt.user_id = u1.id LEFT JOIN address ad1 ON ad1.user_id = u1.id AND ad1.is_default = 1
AND o.code LIKE concat('%',#{code},'%')
AND o.detail LIKE concat('%',#{detail},'%')
AND o.user_id = #{userId}
AND o.product_id = #{productId}
AND o.trade_status = #{tradeStatus}
AND o.refund_status = #{refundStatus}
AND o.create_time BETWEEN #{startTime} AND #{endTime}
ORDER BY o.create_time DESC
LIMIT #{current},#{size}

SELECT COUNT(*)
FROM orders o


        AND o.code LIKE concat('%',#{code},'%')


        AND o.detail LIKE concat('%',#{detail},'%')


        AND o.user_id = #{userId}


        AND o.product_id = #{productId}


        AND o.trade_status = #{tradeStatus}


        AND o.refund_status = #{refundStatus}


        AND o.create_time BETWEEN #{startTime} AND #{endTime}

SELECT o.*, u.user_name, u.user_avatar, p.name AS product_title, p.cover_list AS product_cover FROM orders o LEFT JOIN user u ON u.id = o.user_id LEFT JOIN product p ON p.id = o.product_id WHERE o.product_id IN
#{id}
AND o.trade_status = #{tradeStatus}
AND o.code LIKE concat('%',#{code},'%')
